import './login.css';
function Login() {
    const [data, setdata] = useState({
        name: "",
        number: "",

    });

    const fun = (event) => {
        const value = event.target.value;
        const name = event.target.name;




        setdata((prev) => {
            return {
                ...prev,
                [name]: value,
            }
        })


    };

    const submitted = (e) => {
        e.preventDefault();
        console.log(data);
    };
    // const fun1 = () => {};
    return (
        <div className="App">
            <div className="form-wrapper">
                <div className="left">
                <div className="form">
                <form onSubmit={submitted}>
                    <input
                        type="text"
                        name="name"
                        id=""
                        placeholder="enter the name"
                        value={data.name}
                        onChange={fun}
                    />

                    <input
                        type="number"
                        name="number"
                        placeholder="enter your number"
                        id=""
                        value={data.number}
                        onChange={fun}
                    />


                    <button type="submit">submit</button>
                </form>
            </div>
