import { useState } from "react";
import './login.css';
function Login() {
    const [data, setdata] = useState({
        name: "",
        number: "",

    });

    const fun = (event) => {
        const value = event.target.value;
        const name = event.target.name;

        setdata((prev) => {
            return {
                ...prev,
                [name]: value,
            }
        })


    };

    const submitted = (e) => {
        e.preventDefault();
        console.log(data);
    };
    // const fun1 = () => {};
    return (
        <div className="App">
            <div className="form-wrapper">
                <div className="left">
                    <h1>log in to continue</h1>
                <div className="form">
                <form onSubmit={submitted} className="form-content">
                    <input
                        type="text"
                        name="name"
                        id=""
                        placeholder="enter the name"
                        value={data.name}
                        onChange={fun}
                    />

                    <input
                        type="number"
                        name="number"
                        placeholder="enter your number"
                        id=""
                        value={data.number}
                        onChange={fun}
                    />


                    <button className="
                    submit" type="submit">log in</button>
                     <div className="or">
                    <h1>or</h1>
                </div>
                <button className="google">continue with google</button>
                </form>
               
            </div>
                </div>
                <div className="right">
                    <img src="https://images.unsplash.com/photo-1704972841788-86fac20fc87e?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxlZGl0b3JpYWwtZmVlZHw0fHx8ZW58MHx8fHx8" alt="" />
                </div>
            </div>
        </div>
    );
}

export default Login;
